'add a new chart 
Dim index As Integer = charts.Add(ChartType.Column3DStacked, 5, 0, 15, 5)

'get the newly added chart
Dim chart As Chart = charts(index)

'set charts nseries
chart.NSeries.Add("A1:A3", True)

'Show data labels
chart.NSeries(0).DataLabels.IsValueShown = True

